function Lp = PGA_Frame2(L, M)
% basis vector = ["1", "e0", "e1", "e2", "e3", "e01", "e02", "e03", "e23", "e31", "e12", "e123", "e023", "e031", "e012", "e0123"]
% calculation of M*L*iM, a rigid motion of trivector L by M
% 156M 78A
Lp = [L(1)*(M(1)^2 + M(5)^2 - M(6)^2 - M(7)^2) + 2*( L(2)*M(1)*M(7) - L(3)*M(1)*M(6) + L(5)*M(1)*M(4) - L(6)*M(1)*M(3) + L(4)*M(2)*M(5) + L(2)*M(5)*M(6) - L(4)*M(1)*M(8) - L(4)*M(3)*M(6) + L(5)*M(2)*M(6) + L(5)*M(3)*M(5) + L(3)*M(5)*M(7) - L(4)*M(4)*M(7) + L(6)*M(2)*M(7) + L(6)*M(4)*M(5) - L(5)*M(7)*M(8) + L(6)*M(6)*M(8));
      L(2)*(M(1)^2 - M(5)^2 + M(6)^2 - M(7)^2) + 2*(-L(1)*M(1)*M(7) + L(3)*M(1)*M(5) - L(4)*M(1)*M(4) + L(6)*M(1)*M(2) + L(1)*M(5)*M(6) + L(4)*M(2)*M(6) + L(4)*M(3)*M(5) - L(5)*M(2)*M(5) - L(5)*M(1)*M(8) + L(5)*M(3)*M(6) + L(3)*M(6)*M(7) - L(5)*M(4)*M(7) + L(6)*M(3)*M(7) + L(6)*M(4)*M(6) + L(4)*M(7)*M(8) - L(6)*M(5)*M(8));
      L(3)*(M(1)^2 - M(5)^2 - M(6)^2 + M(7)^2) + 2*( L(1)*M(1)*M(6) - L(2)*M(1)*M(5) + L(4)*M(1)*M(3) - L(5)*M(1)*M(2) + L(1)*M(5)*M(7) + L(4)*M(2)*M(7) + L(4)*M(4)*M(5) - L(6)*M(2)*M(5) + L(2)*M(6)*M(7) + L(5)*M(3)*M(7) + L(5)*M(4)*M(6) - L(6)*M(1)*M(8) - L(6)*M(3)*M(6) + L(6)*M(4)*M(7) - L(4)*M(6)*M(8) + L(5)*M(5)*M(8));
      L(4)*(M(1)^2 + M(5)^2 - M(6)^2 - M(7)^2) + 2*(-L(6)*M(1)*M(6) + L(5)*M(1)*M(7) + L(5)*M(5)*M(6) + L(6)*M(5)*M(7));
      L(5)*(M(1)^2 - M(5)^2 + M(6)^2 - M(7)^2) + 2*( L(6)*M(1)*M(5) - L(4)*M(1)*M(7) + L(4)*M(5)*M(6) + L(6)*M(6)*M(7));
      L(6)*(M(1)^2 - M(5)^2 - M(6)^2 + M(7)^2) + 2*(-L(5)*M(1)*M(5) + L(4)*M(1)*M(6) + L(4)*M(5)*M(7) + L(5)*M(6)*M(7))];
end