function robot = GA_chin()
% The model of the robot
robot.n = 6;
robot.g = [0; 0; -9.81];
robot.urdf = "chin.urdf";

% The motor between adjacent joint frames generated by MDH parameter
robot.Mj(:, 1) = GA_DHmotor([     0;    pi;   -0.283;     0], 1);
robot.Mj(:, 2) = GA_DHmotor([     0;  pi/2;        0;  pi/2], 1);
robot.Mj(:, 3) = GA_DHmotor([  -0.4;    pi;        0;     0], 1);
robot.Mj(:, 4) = GA_DHmotor([ -0.36;    pi; -0.19875; -pi/2], 1);
robot.Mj(:, 5) = GA_DHmotor([     0; -pi/2; -0.16975;     0], 1);
robot.Mj(:, 6) = GA_DHmotor([     0;  pi/2; -0.14875;     0], 1);
robot.Mj(:, 7) = GA_DHmotor([     0;    pi;        0;     0], 1); % end effector is frame {i+1}

% mass and inertial parameters of each link in the mass center frame
Nc(:, 1) = [ 5.675; 1.1632;     0; 0.001; 1.1632; 0; 0.0125];
Nc(:, 2) = [ 5.466;  0.014; 0.009;     0;  0.124; 0;  0.122];
Nc(:, 3) = [ 4.294;   0.01; -0.01;     0;  0.081; 0;  0.076];
Nc(:, 4) = [ 2.732;  0.005;     0;     0;  0.003; 0;  0.005];
Nc(:, 5) = [ 2.732;  0.005;     0;     0;  0.003; 0;  0.005];
Nc(:, 6) = [  0.28;   0.01;     0;     0;   0.01; 0;   0.01];

% Mass center of each link in the joint frame
rc(:, 1) = [     0;  0.043;  0.005];
rc(:, 2) = [-0.298;      0; -0.126];
rc(:, 3) = [-0.274;      0; -0.061];
rc(:, 4) = [     0; -0.055;  0.001];
rc(:, 5) = [     0;  0.055;  0.001];
rc(:, 6) = [     0;      0;  0.017];

% The initial position of the joint axis
for i = 1:robot.n
    robot.Lj(:, i) = [0; 0; 0; 0; 0; 1]; % in the joint frame
    robot.type(i) = 1;
end

% calculate  dynamic parameters in the joint frame
for i = 1:robot.n
    I = [Nc(2, i), Nc(3, i), Nc(4, i); Nc(3, i), Nc(5, i), Nc(6, i); Nc(4, i), Nc(6, i), Nc(7, i)];
    Rc = skew(rc(:, i));
    J = I - Nc(1, i)*Rc*Rc;
    robot.Ij(:, :, i) = [Nc(1, i)*Rc, J; Nc(1, i)*eye(3), -Nc(1, i)*Rc];
end
end