function robot = POE_jaka()
% The model of the robot
robot.n = 6;
robot.g = [0; 0; -9.81];

% The motor between adjacent joint frames generated by MDH parameter
robot.Tj(:, :, 1) = POE_DHmotor([      0;     0; 0.10265; 0], 1);
robot.Tj(:, :, 2) = POE_DHmotor([      0;  pi/2;       0; 0], 1);
robot.Tj(:, :, 3) = POE_DHmotor([  0.595;     0;       0; 0], 1);
robot.Tj(:, :, 4) = POE_DHmotor([ 0.5715;     0; -0.1315; 0], 1);
robot.Tj(:, :, 5) = POE_DHmotor([      0;  pi/2;   0.115; 0], 1);
robot.Tj(:, :, 6) = POE_DHmotor([      0; -pi/2;  0.1035; 0], 1);
robot.Tj(:, :, 7) = POE_DHmotor([      0;     0;       0; 0], 1); % end effector is frame {i+1}

% for i = 1:robot.n
%     rotm2quat(robot.Tj(1:3, 1:3, i))
%     robot.Tj(1:3, 4, i)
% end

% mass and inertial parameters of each link in the mass center frame
Nc(:, 1) = [ 3.78;   0.0404051529004393; -5.35916937084468E-05;  8.56853276426298E-05;   0.0404457306741405;  -0.00039414179882763;  0.0343161527751278];
Nc(:, 2) = [12.44;    0.116456439495809;  -0.00156765863153774; -3.42571922960543E-06;     1.66880550591637; -1.42545474100985E-05;    1.65302926902866];
Nc(:, 3) = [ 5.06;   0.0212898342592158; -1.15196860083347E-05;  -0.00344346825692792;    0.497633875998665; -6.35439617896331E-07;   0.498513225896089];
Nc(:, 4) = [0.883;  0.00458464465961989;  2.63659533420518E-06; -1.52605286509264E-05;  0.00363879515047717; -3.19585101806877E-05; 0.00455871160423887];
Nc(:, 5) = [    1;  0.00702353102292446; -2.72522678679305E-06;  1.52464540194323E-05;  0.00413469714863856;  2.81695088352211E-05; 0.00699581168456812];
Nc(:, 6) = [ 0.22; 0.000597975009893646; -2.41634474044304E-05; -6.20059067536115E-06; 0.000647976470675928; -6.92746756534325E-07; 0.00106463342891045];

% Mass center of each link in the joint frame
rc(:, 1) = [-0.000328431560018958;   0.00406089906856803;  -0.0250109305609668];
rc(:, 2) = [    0.297499939118518; -1.25165127429216E-08;   -0.166069989366573];
rc(:, 3) = [    0.294233782543355; -4.72319252706188E-06;  -0.0241861096678911];
rc(:, 4) = [ 4.22017696188881E-06;   -0.0150343523848632;  0.00216593552372366];
rc(:, 5) = [ 3.86594587120648E-06;   0.00426530078104992; -0.00184283112443297];
rc(:, 6) = [-0.000786084663781494;  3.88801034911165E-05;  -0.0163246941858815];

% The initial position of the joint axis
for i = 1:robot.n
    robot.Lj(:, i) = [0; 0; 1; 0; 0; 0]; % in the joint frame
    robot.type(i) = 1;
end

% calculate  dynamic parameters in the joint frame
for i = 1:robot.n
    I = [Nc(2, i), Nc(3, i), Nc(4, i); Nc(3, i), Nc(5, i), Nc(6, i); Nc(4, i), Nc(6, i), Nc(7, i)];
    Rc = skew(rc(:, i));
    J = I - Nc(1, i)*Rc*Rc;
    robot.Ij(:, :, i) = [J, Nc(1, i)*Rc; -Nc(1, i)*Rc, Nc(1, i)*eye(3)];
end
end